import { useState, useEffect } from 'react';
import styled from 'styled-components';
import ToDoList from '../../molecules/ToDoList';
import ToDoForm from '../../molecules/ToDoForm';

const defaultChangeValue = false;

let defaultList = [
{text: 'Hello'},
{text: 'WE WERE ALIVE'}
]

const Wrapper = styled.div`
  max-width: 400px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled.h1`
  font-size: 20px;
`;

const ToDoPage = () => {
    const [list, setList] = useState(defaultList);
    const [listUnSaved, setListUnsaved] = useState(defaultChangeValue);
    const onAddNewListItem = (value) => {
        setList([...list, { text: value }]);
    };

    useEffect(() => {
        if (JSON.stringify(list) === JSON.stringify(defaultList)) {
            setListUnsaved(false);
        } else {
            setListUnsaved(true);
        }
    });

    const resetList = () => {
        setList(defaultList);
    }

    const onSaveList = () => {
        defaultList = list;
        setListUnsaved(false);
    }

    const onRemove = (i) => {
        setList(list.filter((elem, index) => i !== index));
    }

    return (
        <Wrapper>
            <Title>List of tasks</Title>
            <ToDoList list={list} onRemove={onRemove() } />
            <ToDoForm onSubmit={onAddNewListItem} />
            {listUnSaved && (
                <div>
                    List not saved
                    {/* <Button>Dismiss</Button> */}
                    {/* <Button primary>Save</Button> */}
                </div>
            )}
        </Wrapper>
    );
};

export default ToDoPage;
